import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = () => {
  //   const [userInput, setUserInput] = useState({
  //     enteredTitle: "",
  //     enteredAmount: "",
  //     enteredDate: "",
  //   });
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const titleChangeHandler = (event) => {
    setEnteredTitle(event.input.value);

    // setUserInput({
    //   ...userInput,
    //   enteredTitle: event.input.value,
    // });

    // setUserInput((prevState) => {
    //   return { ...prevState, enteredTitle: event.input.value };
    // });
    console.log(event.target.value);
  };

  const amountChangeHandler = (event) => {
    setEnteredAmount(event.input.value);

    // setUserInput({
    //   ...userInput,
    //   enteredAmount: event.input.value,
    // });

    // setUserInput((prevState) => {
    //   return { ...prevState, enteredAmount: event.input.value };
    // });
    console.log(event.target.value);
  };

  const dateChangeHandler = (event) => {
    setEnteredDate(event.input.value);

    // setUserInput({
    //   ...userInput,
    //   enteredDate: event.input.value,
    // });

    // setUserInput((prevState) => {
    //   return { ...prevState, enteredDate: event.input.value };
    // });
    console.log(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };
    console.log(expenseData);
  };

  return (
    <form onSubmit={submitHandler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler}></input>
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" onChange={amountChangeHandler}></input>
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            min="2019-01-01"
            max="2023-12-31"
            onChange={dateChangeHandler}
          ></input>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
